  bool access::object_traits_impl< ::DuaBytes, id_pgsql >::
  grow (image_type& i,
        bool* t)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (t);

    bool grew (false);

    // id
    //
    t[0UL] = 0;

    // dua
    //
    t[1UL] = 0;

    // selected
    //
    if (composite_value_traits< ::Range, id_pgsql >::grow (
          i.selected_value, t + 2UL))
      grew = true;

    // all_labels
    //
    if (t[4UL])
    {
      i.all_labels_value.capacity (i.all_labels_size);
      grew = true;
    }

    return grew;
  }

  void access::object_traits_impl< ::DuaBytes, id_pgsql >::
  bind (pgsql::bind* b,
        image_type& i,
        pgsql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace pgsql;

    std::size_t n (0);

    // id
    //
    if (sk != statement_insert && sk != statement_update)
    {
      b[n].type = pgsql::bind::bigint;
      b[n].buffer = &i.id_value;
      b[n].is_null = &i.id_null;
      n++;
    }

    // dua
    //
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.dua_value;
    b[n].is_null = &i.dua_null;
    n++;

    // selected
    //
    composite_value_traits< ::Range, id_pgsql >::bind (
      b + n, i.selected_value, sk);
    n += 2UL;

    // all_labels
    //
    b[n].type = pgsql::bind::text;
    b[n].buffer = i.all_labels_value.data ();
    b[n].capacity = i.all_labels_value.capacity ();
    b[n].size = &i.all_labels_size;
    b[n].is_null = &i.all_labels_null;
    n++;
  }

  void access::object_traits_impl< ::DuaBytes, id_pgsql >::
  bind (pgsql::bind* b, id_image_type& i)
  {
    std::size_t n (0);
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.id_value;
    b[n].is_null = &i.id_null;
  }

  bool access::object_traits_impl< ::DuaBytes, id_pgsql >::
  init (image_type& i,
        const object_type& o,
        pgsql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace pgsql;

    bool grew (false);

    // dua
    //
    {
      ::Dua const* const& v =
        o.dua;

      typedef object_traits< ::Dua > obj_traits;
      typedef odb::pointer_traits< ::Dua const* > ptr_traits;

      bool is_null (ptr_traits::null_ptr (v));
      if (!is_null)
      {
        const obj_traits::id_type& id (
          obj_traits::id (ptr_traits::get_ref (v)));

        pgsql::value_traits<
            obj_traits::id_type,
            pgsql::id_bigint >::set_image (
          i.dua_value, is_null, id);
        i.dua_null = is_null;
      }
      else
        throw null_pointer ();
    }

    // selected
    //
    {
      ::Range const& v =
        o.selected;

      composite_value_traits< ::Range, id_pgsql >::init (
        i.selected_value,
        v,
        sk);
    }

    // all_labels
    //
    {
      ::std::vector< unsigned int > const& v =
        o.all_labels;

      bool is_null (false);
      std::size_t size (0);
      std::size_t cap (i.all_labels_value.capacity ());
      pgsql::value_traits<
          ::std::vector< unsigned int >,
          pgsql::id_string >::set_image (
        i.all_labels_value,
        size,
        is_null,
        v);
      i.all_labels_null = is_null;
      i.all_labels_size = size;
      grew = grew || (cap != i.all_labels_value.capacity ());
    }

    return grew;
  }

  void access::object_traits_impl< ::DuaBytes, id_pgsql >::
  init (object_type& o,
        const image_type& i,
        database* db)
  {
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (db);

    // id
    //
    {
      ::uint64_t& v =
        o.id;

      pgsql::value_traits<
          ::uint64_t,
          pgsql::id_bigint >::set_value (
        v,
        i.id_value,
        i.id_null);
    }

    // dua
    //
    {
      ::Dua const*& v =
        o.dua;

      typedef object_traits< ::Dua > obj_traits;
      typedef odb::pointer_traits< ::Dua const* > ptr_traits;

      if (i.dua_null)
        v = ptr_traits::pointer_type ();
      else
      {
        obj_traits::id_type id;
        pgsql::value_traits<
            obj_traits::id_type,
            pgsql::id_bigint >::set_value (
          id,
          i.dua_value,
          i.dua_null);

        // If a compiler error points to the line below, then
        // it most likely means that a pointer used in a member
        // cannot be initialized from an object pointer.
        //
        v = ptr_traits::pointer_type (
          static_cast<pgsql::database*> (db)->load<
            obj_traits::object_type > (id));
      }
    }

    // selected
    //
    {
      ::Range& v =
        o.selected;

      composite_value_traits< ::Range, id_pgsql >::init (
        v,
        i.selected_value,
        db);
    }

    // all_labels
    //
    {
      ::std::vector< unsigned int >& v =
        o.all_labels;

      pgsql::value_traits<
          ::std::vector< unsigned int >,
          pgsql::id_string >::set_value (
        v,
        i.all_labels_value,
        i.all_labels_size,
        i.all_labels_null);
    }
  }

