  access::view_traits_impl< ::BugLval, id_pgsql >::
  query_statement (const query_base_type& q)
  {
    query_base_type r (
      "SELECT DISTINCT "
      "\"bug\".\"trigger_lval\" ");

    r += "FROM \"bug\"";

    query_base_type c (
      // From lava.hxx:422:5
      (q.empty () ? query_base_type::true_expr : q) + "ORDER BY" + query_columns::trigger_lval);

    c.optimize ();

    if (!c.empty ())
    {
      r += " ";
      r += c.clause_prefix ();
      r += c;
    }

    return r;
  }

