  bool access::object_traits_impl< ::Run, id_pgsql >::
  grow (image_type& i,
        bool* t)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (t);

    bool grew (false);

    // id
    //
    t[0UL] = 0;

    // build
    //
    t[1UL] = 0;

    // fuzzed
    //
    t[2UL] = 0;

    // exitcode
    //
    t[3UL] = 0;

    // output
    //
    if (t[4UL])
    {
      i.output_value.capacity (i.output_size);
      grew = true;
    }

    // success
    //
    t[5UL] = 0;

    // validated
    //
    t[6UL] = 0;

    return grew;
  }

  void access::object_traits_impl< ::Run, id_pgsql >::
  bind (pgsql::bind* b,
        image_type& i,
        pgsql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace pgsql;

    std::size_t n (0);

    // id
    //
    if (sk != statement_insert && sk != statement_update)
    {
      b[n].type = pgsql::bind::bigint;
      b[n].buffer = &i.id_value;
      b[n].is_null = &i.id_null;
      n++;
    }

    // build
    //
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.build_value;
    b[n].is_null = &i.build_null;
    n++;

    // fuzzed
    //
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.fuzzed_value;
    b[n].is_null = &i.fuzzed_null;
    n++;

    // exitcode
    //
    b[n].type = pgsql::bind::integer;
    b[n].buffer = &i.exitcode_value;
    b[n].is_null = &i.exitcode_null;
    n++;

    // output
    //
    b[n].type = pgsql::bind::text;
    b[n].buffer = i.output_value.data ();
    b[n].capacity = i.output_value.capacity ();
    b[n].size = &i.output_size;
    b[n].is_null = &i.output_null;
    n++;

    // success
    //
    b[n].type = pgsql::bind::boolean_;
    b[n].buffer = &i.success_value;
    b[n].is_null = &i.success_null;
    n++;

    // validated
    //
    b[n].type = pgsql::bind::boolean_;
    b[n].buffer = &i.validated_value;
    b[n].is_null = &i.validated_null;
    n++;
  }

  void access::object_traits_impl< ::Run, id_pgsql >::
  bind (pgsql::bind* b, id_image_type& i)
  {
    std::size_t n (0);
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.id_value;
    b[n].is_null = &i.id_null;
  }

  bool access::object_traits_impl< ::Run, id_pgsql >::
  init (image_type& i,
        const object_type& o,
        pgsql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace pgsql;

    bool grew (false);

    // build
    //
    {
      ::Build const* const& v =
        o.build;

      typedef object_traits< ::Build > obj_traits;
      typedef odb::pointer_traits< ::Build const* > ptr_traits;

      bool is_null (ptr_traits::null_ptr (v));
      if (!is_null)
      {
        const obj_traits::id_type& id (
          obj_traits::id (ptr_traits::get_ref (v)));

        pgsql::value_traits<
            obj_traits::id_type,
            pgsql::id_bigint >::set_image (
          i.build_value, is_null, id);
        i.build_null = is_null;
      }
      else
        throw null_pointer ();
    }

    // fuzzed
    //
    {
      ::Bug const* const& v =
        o.fuzzed;

      typedef object_traits< ::Bug > obj_traits;
      typedef odb::pointer_traits< ::Bug const* > ptr_traits;

      bool is_null (ptr_traits::null_ptr (v));
      if (!is_null)
      {
        const obj_traits::id_type& id (
          obj_traits::id (ptr_traits::get_ref (v)));

        pgsql::value_traits<
            obj_traits::id_type,
            pgsql::id_bigint >::set_image (
          i.fuzzed_value, is_null, id);
        i.fuzzed_null = is_null;
      }
      else
        i.fuzzed_null = true;
    }

    // exitcode
    //
    {
      int const& v =
        o.exitcode;

      bool is_null (false);
      pgsql::value_traits<
          int,
          pgsql::id_integer >::set_image (
        i.exitcode_value, is_null, v);
      i.exitcode_null = is_null;
    }

    // output
    //
    {
      ::std::string const& v =
        o.output;

      bool is_null (false);
      std::size_t size (0);
      std::size_t cap (i.output_value.capacity ());
      pgsql::value_traits<
          ::std::string,
          pgsql::id_string >::set_image (
        i.output_value,
        size,
        is_null,
        v);
      i.output_null = is_null;
      i.output_size = size;
      grew = grew || (cap != i.output_value.capacity ());
    }

    // success
    //
    {
      bool const& v =
        o.success;

      bool is_null (false);
      pgsql::value_traits<
          bool,
          pgsql::id_boolean >::set_image (
        i.success_value, is_null, v);
      i.success_null = is_null;
    }

    // validated
    //
    {
      bool const& v =
        o.validated;

      bool is_null (false);
      pgsql::value_traits<
          bool,
          pgsql::id_boolean >::set_image (
        i.validated_value, is_null, v);
      i.validated_null = is_null;
    }

    return grew;
  }

  void access::object_traits_impl< ::Run, id_pgsql >::
  init (object_type& o,
        const image_type& i,
        database* db)
  {
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (db);

    // id
    //
    {
      ::uint64_t& v =
        o.id;

      pgsql::value_traits<
          ::uint64_t,
          pgsql::id_bigint >::set_value (
        v,
        i.id_value,
        i.id_null);
    }

    // build
    //
    {
      ::Build const*& v =
        o.build;

      typedef object_traits< ::Build > obj_traits;
      typedef odb::pointer_traits< ::Build const* > ptr_traits;

      if (i.build_null)
        v = ptr_traits::pointer_type ();
      else
      {
        obj_traits::id_type id;
        pgsql::value_traits<
            obj_traits::id_type,
            pgsql::id_bigint >::set_value (
          id,
          i.build_value,
          i.build_null);

        // If a compiler error points to the line below, then
        // it most likely means that a pointer used in a member
        // cannot be initialized from an object pointer.
        //
        v = ptr_traits::pointer_type (
          static_cast<pgsql::database*> (db)->load<
            obj_traits::object_type > (id));
      }
    }

    // fuzzed
    //
    {
      ::Bug const*& v =
        o.fuzzed;

      typedef object_traits< ::Bug > obj_traits;
      typedef odb::pointer_traits< ::Bug const* > ptr_traits;

      if (i.fuzzed_null)
        v = ptr_traits::pointer_type ();
      else
      {
        obj_traits::id_type id;
        pgsql::value_traits<
            obj_traits::id_type,
            pgsql::id_bigint >::set_value (
          id,
          i.fuzzed_value,
          i.fuzzed_null);

        // If a compiler error points to the line below, then
        // it most likely means that a pointer used in a member
        // cannot be initialized from an object pointer.
        //
        v = ptr_traits::pointer_type (
          static_cast<pgsql::database*> (db)->load<
            obj_traits::object_type > (id));
      }
    }

    // exitcode
    //
    {
      int& v =
        o.exitcode;

      pgsql::value_traits<
          int,
          pgsql::id_integer >::set_value (
        v,
        i.exitcode_value,
        i.exitcode_null);
    }

    // output
    //
    {
      ::std::string& v =
        o.output;

      pgsql::value_traits<
          ::std::string,
          pgsql::id_string >::set_value (
        v,
        i.output_value,
        i.output_size,
        i.output_null);
    }

    // success
    //
    {
      bool& v =
        o.success;

      pgsql::value_traits<
          bool,
          pgsql::id_boolean >::set_value (
        v,
        i.success_value,
        i.success_null);
    }

    // validated
    //
    {
      bool& v =
        o.validated;

      pgsql::value_traits<
          bool,
          pgsql::id_boolean >::set_value (
        v,
        i.validated_value,
        i.validated_null);
    }
  }

