  bool access::object_traits_impl< ::Call, id_pgsql >::
  grow (image_type& i,
        bool* t)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (t);

    bool grew (false);

    // id
    //
    t[0UL] = 0;

    // call_instr
    //
    t[1UL] = 0;

    // ret_instr
    //
    t[2UL] = 0;

    // called_function
    //
    t[3UL] = 0;

    // callsite_file
    //
    if (t[4UL])
    {
      i.callsite_file_value.capacity (i.callsite_file_size);
      grew = true;
    }

    // callsite_line
    //
    t[5UL] = 0;

    return grew;
  }

  void access::object_traits_impl< ::Call, id_pgsql >::
  bind (pgsql::bind* b,
        image_type& i,
        pgsql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace pgsql;

    std::size_t n (0);

    // id
    //
    if (sk != statement_insert && sk != statement_update)
    {
      b[n].type = pgsql::bind::bigint;
      b[n].buffer = &i.id_value;
      b[n].is_null = &i.id_null;
      n++;
    }

    // call_instr
    //
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.call_instr_value;
    b[n].is_null = &i.call_instr_null;
    n++;

    // ret_instr
    //
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.ret_instr_value;
    b[n].is_null = &i.ret_instr_null;
    n++;

    // called_function
    //
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.called_function_value;
    b[n].is_null = &i.called_function_null;
    n++;

    // callsite_file
    //
    b[n].type = pgsql::bind::text;
    b[n].buffer = i.callsite_file_value.data ();
    b[n].capacity = i.callsite_file_value.capacity ();
    b[n].size = &i.callsite_file_size;
    b[n].is_null = &i.callsite_file_null;
    n++;

    // callsite_line
    //
    b[n].type = pgsql::bind::integer;
    b[n].buffer = &i.callsite_line_value;
    b[n].is_null = &i.callsite_line_null;
    n++;
  }

  void access::object_traits_impl< ::Call, id_pgsql >::
  bind (pgsql::bind* b, id_image_type& i)
  {
    std::size_t n (0);
    b[n].type = pgsql::bind::bigint;
    b[n].buffer = &i.id_value;
    b[n].is_null = &i.id_null;
  }

  bool access::object_traits_impl< ::Call, id_pgsql >::
  init (image_type& i,
        const object_type& o,
        pgsql::statement_kind sk)
  {
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (sk);

    using namespace pgsql;

    bool grew (false);

    // call_instr
    //
    {
      ::uint64_t const& v =
        o.call_instr;

      bool is_null (false);
      pgsql::value_traits<
          ::uint64_t,
          pgsql::id_bigint >::set_image (
        i.call_instr_value, is_null, v);
      i.call_instr_null = is_null;
    }

    // ret_instr
    //
    {
      ::uint64_t const& v =
        o.ret_instr;

      bool is_null (false);
      pgsql::value_traits<
          ::uint64_t,
          pgsql::id_bigint >::set_image (
        i.ret_instr_value, is_null, v);
      i.ret_instr_null = is_null;
    }

    // called_function
    //
    {
      ::SourceFunction const* const& v =
        o.called_function;

      typedef object_traits< ::SourceFunction > obj_traits;
      typedef odb::pointer_traits< ::SourceFunction const* > ptr_traits;

      bool is_null (ptr_traits::null_ptr (v));
      if (!is_null)
      {
        const obj_traits::id_type& id (
          obj_traits::id (ptr_traits::get_ref (v)));

        pgsql::value_traits<
            obj_traits::id_type,
            pgsql::id_bigint >::set_image (
          i.called_function_value, is_null, id);
        i.called_function_null = is_null;
      }
      else
        throw null_pointer ();
    }

    // callsite_file
    //
    {
      ::std::string const& v =
        o.callsite_file;

      bool is_null (false);
      std::size_t size (0);
      std::size_t cap (i.callsite_file_value.capacity ());
      pgsql::value_traits<
          ::std::string,
          pgsql::id_string >::set_image (
        i.callsite_file_value,
        size,
        is_null,
        v);
      i.callsite_file_null = is_null;
      i.callsite_file_size = size;
      grew = grew || (cap != i.callsite_file_value.capacity ());
    }

    // callsite_line
    //
    {
      ::uint32_t const& v =
        o.callsite_line;

      bool is_null (false);
      pgsql::value_traits<
          ::uint32_t,
          pgsql::id_integer >::set_image (
        i.callsite_line_value, is_null, v);
      i.callsite_line_null = is_null;
    }

    return grew;
  }

  void access::object_traits_impl< ::Call, id_pgsql >::
  init (object_type& o,
        const image_type& i,
        database* db)
  {
    ODB_POTENTIALLY_UNUSED (o);
    ODB_POTENTIALLY_UNUSED (i);
    ODB_POTENTIALLY_UNUSED (db);

    // id
    //
    {
      ::uint64_t& v =
        o.id;

      pgsql::value_traits<
          ::uint64_t,
          pgsql::id_bigint >::set_value (
        v,
        i.id_value,
        i.id_null);
    }

    // call_instr
    //
    {
      ::uint64_t& v =
        o.call_instr;

      pgsql::value_traits<
          ::uint64_t,
          pgsql::id_bigint >::set_value (
        v,
        i.call_instr_value,
        i.call_instr_null);
    }

    // ret_instr
    //
    {
      ::uint64_t& v =
        o.ret_instr;

      pgsql::value_traits<
          ::uint64_t,
          pgsql::id_bigint >::set_value (
        v,
        i.ret_instr_value,
        i.ret_instr_null);
    }

    // called_function
    //
    {
      ::SourceFunction const*& v =
        o.called_function;

      typedef object_traits< ::SourceFunction > obj_traits;
      typedef odb::pointer_traits< ::SourceFunction const* > ptr_traits;

      if (i.called_function_null)
        v = ptr_traits::pointer_type ();
      else
      {
        obj_traits::id_type id;
        pgsql::value_traits<
            obj_traits::id_type,
            pgsql::id_bigint >::set_value (
          id,
          i.called_function_value,
          i.called_function_null);

        // If a compiler error points to the line below, then
        // it most likely means that a pointer used in a member
        // cannot be initialized from an object pointer.
        //
        v = ptr_traits::pointer_type (
          static_cast<pgsql::database*> (db)->load<
            obj_traits::object_type > (id));
      }
    }

    // callsite_file
    //
    {
      ::std::string& v =
        o.callsite_file;

      pgsql::value_traits<
          ::std::string,
          pgsql::id_string >::set_value (
        v,
        i.callsite_file_value,
        i.callsite_file_size,
        i.callsite_file_null);
    }

    // callsite_line
    //
    {
      ::uint32_t& v =
        o.callsite_line;

      pgsql::value_traits<
          ::uint32_t,
          pgsql::id_integer >::set_value (
        v,
        i.callsite_line_value,
        i.callsite_line_null);
    }
  }

